package metadata_url

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

func Test_GetDirectURL(t *testing.T) {
	New("https://ipfs.rss3.page/ipfs/")

	type arguments struct {
		directUrl string
	}

	testcases := []struct {
		name      string
		arguments arguments
		want      string
	}{
		{
			name: "/ipfs/",
			arguments: arguments{
				directUrl: "https://ipfs.infura.io/ipfs/Qmc1rg7zRim6z6snsZz5bj2PkEek9F9w1szGBsgd2JF95L",
			},
			want: "https://ipfs.rss3.page/ipfs/Qmc1rg7zRim6z6snsZz5bj2PkEek9F9w1szGBsgd2JF95L",
		},
		{
			name: "ipfs://",
			arguments: arguments{
				directUrl: "ipfs://QmUYtXQkDLiTBADz6wEHg6D79JY5cRwGVBygWfrxSCKPnW/nft.jpg",
			},
			want: "https://ipfs.rss3.page/ipfs/QmUYtXQkDLiTBADz6wEHg6D79JY5cRwGVBygWfrxSCKPnW/nft.jpg",
		},
		{
			name: "ar://",
			arguments: arguments{
				directUrl: "ar://0v4xWPa_B3ILV4NZYip5vA2-x01h540oEzhVco4s1z4/956",
			},
			want: "https://arweave.net/0v4xWPa_B3ILV4NZYip5vA2-x01h540oEzhVco4s1z4/956",
		},
		{
			name: "base64",
			arguments: arguments{
				directUrl: "data:application/json;base64,",
			},
			want: "data:application/json;base64,",
		},
	}

	for _, testcase := range testcases {
		t.Run(testcase.name, func(t *testing.T) {
			result := GetDirectURL(testcase.arguments.directUrl)

			assert.Equal(t, testcase.want, result)
		})
	}
}
